package com.syntax.class03;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String dayTime = "morning";
		int date = 22;
		String month = "January";
		// Today is morning of January 22.
		// +used to attach String yo anything

		System.out.println("Today is " + dayTime + " of " + month + " " + date);

		// another way to do it
		String sentence = "Today is " + dayTime + " of " + month + " " + date;
		System.out.println(sentence);

		System.out.println(2022); //int data type
		System.out.println("2022"); // string data type
		int year=2022;
		String year1="2022";
		
		System.out.println(year); //int data type 2022
		System.out.println(year1);//string data type 2022
		System.out.println(year+10); // int data type 2022+10=2031
		System.out.println(year1+10);//string data type 202210
		
		
		
		
	}

}