package com.syntax.class04;

public class ClassReviewPractices {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int i=10;
	i+=100;
	System.out.println(i);//i=10+110
	
	//casting changing a value to one type to another type/There is a two type of casting

	//10 is int number we want to change to double so;
	double d=10;//widening
	System.out.println(d);// out put:10.0
	//narrowing
	// 10.99; is double value we want to change to int
	int in=(int)10.99; //narrowing
	System.out.println(in);//output:10
	//conditional statement
	//1.if statement
	//2switch case
	}
	
	

}
